.container {
	max-width: 120rem;
	margin: 8rem auto;
	background-color: var(--color-grey-light-2);
	box-shadow: var(--shadow-darg);

	min-height: 50rem;

	@media only screen and (max-width: 1200px) {
		margin: 0;
		max-width: 100%;
		width: 100%;
	}
}

.header {
	font-size: 1.4rem;
	height: 7rem;
	background-color: #fff;
	border-bottom: 1px solid var(--color-grey-light-2);
	display: flex;
	justify-content: space-between;
	align-items: center;

	@media only screen and (max-width: $bp-smallest) {
		flex-wrap: wrap;
		align-content: space-around;
		height: 11rem;
	}
}

.content {
	display: flex;

	@media only screen and (max-width: $bp-medium) {
		flex-direction: column;
	}
}

.sidebar {
	background-color: var(--color-grey-dark-1);

	flex: 0 0 18%;

	display: flex;
	flex-direction: column;
	justify-content: space-between;
}

.hotel-view {
	background-color: #fff;

	flex: 1;
}

///////////////////////
.detail {
	font-size: 1.4rem;
	display: flex;
	padding: 4.5rem;
	background-color: var(--color-grey-light-1);
	border-bottom: 1px solid var(--color-grey-light-2);

	@media only screen and (max-width: $bp-medium) {
		padding: 3rem;
	}

	@media only screen and (max-width: $bp-small) {
		flex-direction: column;
	}
}

.description {
	background-color: white;
	box-shadow: 0 2rem 5rem rgba(0, 0, 0, 0.06);
	padding: 3rem;
	flex: 0 0 60%;
	margin-right: 4.5rem;

	@media only screen and (max-width: $bp-medium) {
		padding: 2rem;
		margin-right: 3rem;
	}

	@media only screen and (max-width: $bp-small) {
		margin-right: 0;
		margin-bottom: 3rem;
	}
}

.user-reviews {
	flex: 1;

	display: flex;
	flex-direction: column;
	align-items: center;
}
